    let เพิ่มเคาน์เตอร์ทีละหนึ่ง = () => {
        เซ็ตเคาน์เตอร์(เคาน์เตอร์ + 1)
    }

    return (
        <button onClick={เพิ่มเคาน์เตอร์ทีละหนึ่ง}>
            {เคาน์เตอร์}
        </button>
    )
}

